mapper.insert:
/*
insertSampleTransacation sql */
INSERT INTO TB_SAMPLE
( ID
, NAME
, DESCRIPTION
, USE_YN
, REG_USER
)
VALUES
( #{ID}
, #{NAME}
, #{DESCRIPTION}
, #{USE_YN}
, #{REG_USER}
)

mapper.update:
/*
updateSampleTransacation sql */
UPDATE TB_SAMPLE
SET NAME = #{NAME}
, DESCRIPTION = #{DESCRIPTION}
, USE_YN = #{USE_YN}
, REG_USER = #{REG_USER}
WHERE ID = #{ID}

mapper.delete:
/* deleteSampleTransacation sql */
DELETE
FROM TB_SAMPLE
WHERE ID = #{ID}
